-- add a clustered_id column to Dish
ALTER TABLE Dish ADD COLUMN clustered_id INTEGER;
-- duplicate the Id attribute to the clsuteted_id attribute
UPDATE Dish SET clustered_id = id;
SELECT changes(); -- 423397

-- perform the de-duplication work on Dish

-- create an Intermediate Dish table where we will store the minimum ID value present for this clusterm, as well as recalculated values for Dishes
INSERT INTO DishIntermediate (clustered_id, name_clean, menus_appeared, times_Appeared, first_appeared, last_appeared, lowest_price, highest_price) SELECT MIN(id) as clustered_id, name_clean, 0 as menus_appeared, 0 as times_appeared, NULL as first_appeared, NULL as last_appeared, NULL as lowest_price, NULL as highest_price FROM Dish GROUP BY name_clean;

-- populate menu intermediate
INSERT INTO MenuIntermediate (id, dish_count) SELECT id, dish_count FROM Menu;

-- update all names to use only the cleaned (clustered) names
UPDATE Dish SET name = name_clean;
SELECT changes(); -- 423397

-- propagate that ID decision back to the clustered_id property on the Dish table
-- use an UPSERT to accomplish this in a time-efficient way
INSERT INTO Dish (id, clustered_id)
SELECT d.id, d2.clustered_id FROM Dish AS d INNER JOIN DishIntermediate AS d2 ON d.name_clean=d2.name_clean
ON CONFLICT(id) DO UPDATE SET clustered_id=excluded.clustered_id WHERE id = excluded.id AND clustered_id != excluded.clustered_id;
SELECT changes(); -- 236813

-- propagate the clustered ID from Dish onto the related entries in MenuItem so that dish_id now references the clustered_id
-- use an UPSERT to accomplish this in a time-efficient way
INSERT INTO MenuItem (id, dish_id)
SELECT mi.id, d.clustered_id FROM MenuItem mi INNER JOIN Dish d ON mi.dish_id = d.id
ON CONFLICT(id) DO UPDATE SET dish_id=excluded.dish_id WHERE id = excluded.id AND dish_id!=excluded.dish_id;
SELECT changes(); -- 598290

-- now we can delete all rows where id != clustered_id from Dish
DELETE FROM Dish WHERE id != clustered_id;
SELECT changes(); -- 236813


-- menus_appeared RECALC WITH UPSERT
INSERT INTO DishIntermediate (clustered_id, menus_appeared)
SELECT X.clustered_id, COUNT(*) as menus_appeared FROM (
	SELECT DISTINCT d.clustered_id, m.id as menu_id FROM DishIntermediate d INNER JOIN MenuItem mi on d.clustered_id = mi.dish_id INNER JOIN MenuPage mp ON mi.menu_page_id = mp.id INNER JOIN Menu m on mp.menu_id = m.id
) X
GROUP BY X.clustered_id
ON CONFLICT(clustered_id) DO UPDATE SET menus_appeared=excluded.menus_appeared WHERE clustered_id = excluded.clustered_id;

-- times_appeared RECALC WITH UPSERT
INSERT INTO DishIntermediate (clustered_id, times_appeared)
SELECT X.clustered_id, COUNT(*) as times_appeared FROM (
	SELECT DISTINCT d.clustered_id, mi.id as menu_item_id FROM DishIntermediate d INNER JOIN MenuItem mi on d.clustered_id = mi.dish_id
) X
GROUP BY X.clustered_id
ON CONFLICT(clustered_id) DO UPDATE SET times_appeared=excluded.times_appeared WHERE clustered_id = excluded.clustered_id;

-- first_appeared RECALC WITH UPSERT
INSERT INTO DishIntermediate (clustered_id, first_appeared)
SELECT X.clustered_id, strftime('%Y',MIN(date_clean)) as first_appeared FROM (
	SELECT DISTINCT d.clustered_id, datetime(date_clean) as date_clean FROM DishIntermediate d INNER JOIN MenuItem mi on d.clustered_id = mi.dish_id INNER JOIN MenuPage mp ON mi.menu_page_id = mp.id INNER JOIN Menu m on mp.menu_id = m.id
	WHERE datetime(date) >= datetime('1850-01-01') AND datetime(date) <= datetime('2023-08-01')
) X
GROUP BY X.clustered_id
ON CONFLICT(clustered_id) DO UPDATE SET first_appeared=excluded.first_appeared WHERE clustered_id = excluded.clustered_id;

-- last_appeared RECALC WITH UPSERT
INSERT INTO DishIntermediate (clustered_id, last_appeared)
SELECT X.clustered_id, strftime('%Y',MAX(date_clean)) as last_appeared FROM (
	SELECT DISTINCT d.clustered_id, datetime(date_clean) as date_clean FROM DishIntermediate d INNER JOIN MenuItem mi on d.clustered_id = mi.dish_id INNER JOIN MenuPage mp ON mi.menu_page_id = mp.id INNER JOIN Menu m on mp.menu_id = m.id
	WHERE datetime(date) >= datetime('1850-01-01') AND datetime(date) <= datetime('2023-08-01')
) X
GROUP BY X.clustered_id
ON CONFLICT(clustered_id) DO UPDATE SET last_appeared=excluded.last_appeared WHERE clustered_id = excluded.clustered_id;

-- lowest_price RECALC WITH UPSERT
INSERT INTO DishIntermediate (clustered_id, lowest_price)
SELECT X.clustered_id, MIN(price) as lowest_price FROM (
	SELECT DISTINCT d.clustered_id, mi.price FROM DishIntermediate d INNER JOIN MenuItem mi on d.clustered_id = mi.dish_id WHERE mi.price IS NOT NULL AND mi.price != ''
	UNION
	SELECT DISTINCT d.clustered_id, mi.high_price as price FROM DishIntermediate d INNER JOIN MenuItem mi on d.clustered_id = mi.dish_id WHERE mi.high_price IS NOT NULL AND mi.high_price != ''
) X
GROUP BY X.clustered_id
ON CONFLICT(clustered_id) DO UPDATE SET lowest_price=excluded.lowest_price WHERE clustered_id = excluded.clustered_id;

-- highest_price RECALC WITH UPSERT
INSERT INTO DishIntermediate (clustered_id, highest_price)
SELECT X.clustered_id, MAX(price) as highest_price FROM (
	SELECT DISTINCT d.clustered_id, mi.price FROM DishIntermediate d INNER JOIN MenuItem mi on d.clustered_id = mi.dish_id WHERE mi.price IS NOT NULL AND mi.price != ''
	UNION
	SELECT DISTINCT d.clustered_id, mi.high_price as price FROM DishIntermediate d INNER JOIN MenuItem mi on d.clustered_id = mi.dish_id WHERE mi.high_price IS NOT NULL AND mi.high_price != ''
) X
GROUP BY X.clustered_id
ON CONFLICT(clustered_id) DO UPDATE SET highest_price=excluded.highest_price WHERE clustered_id = excluded.clustered_id;

